INSERT INTO fun_facts (fact, created_at) VALUES
  ('Rekordowa Emerytura i staż pracy: Najwyższa wypłacana emerytura w Polsce, według danych ZUS z listopada 2024 r., wynosiła 48 700 zł. Otrzymuje ją mieszkaniec Śląska, który przeszedł na świadczenie w wieku 86 lat, mając za sobą aż 67 lat pracy i ani razu nie korzystał ze zwolnienia lekarskiego (L4).', NOW()),
  ('Kapitał rekordzisty: Rekordzista z najwyższą emeryturą w Polsce zgromadził na swoim koncie emerytalnym w ZUS kapitał w wysokości 2,4 mln zł.', NOW()),
  ('Prognoza rekordowej waloryzacji (2025): Rekordowa emerytura wynosząca 48 700 zł wzrośnie po waloryzacji w marcu 2025 r. o 2834 zł, osiągając kwotę 51 534 zł.', NOW()),
  ('Średnia emerytura w 2024 r.: Średnia emerytura wypłacana przez ZUS po waloryzacji w marcu 2024 r. wyniosła 3789,60 zł brutto.', NOW()),
  ('Różnica między emeryturami kobiet a mężczyzn (2024): W marcu 2024 r. średnia emerytura kobiet wynosiła 3208,70 zł brutto, podczas gdy średnia emerytura mężczyzn była wyższa i wyniosła 4674,51 zł brutto, co oznacza różnicę w wysokości blisko 1500 zł.', NOW()),
  ('Emerytury niższe niż minimalna (2024): Na koniec grudnia 2024 r. świadczenie emerytalne niższe od minimalnej emerytury (1780,96 zł brutto w tym okresie) pobierało łącznie 433,1 tys. osób.', NOW()),
  ('Udział kobiet w niskich emeryturach (2024): W grupie osób pobierających w grudniu 2024 r. emerytury niższe od najniższej emerytury, większość stanowiły kobiety – było ich 335,3 tys., a mężczyzn 97,8 tys.', NOW()),
  ('Wzrost liczby ubezpieczonych cudzoziemców (2024): Na koniec 2024 r. w ZUS było zarejestrowanych ponad 1 mln 192,9 tys. obcokrajowców, co oznaczało wzrost o 5,8% w stosunku do końca 2023 r.', NOW()),
  ('Dominująca narodowość w ZUS (2024): Najliczniejszą grupą cudzoziemców ubezpieczonych w ZUS na koniec 2024 r. byli obywatele Ukrainy, których liczba wyniosła 787,5 tys. osób.', NOW()),
  ('Forma zatrudnienia cudzoziemców (2024): Na koniec 2024 r. większość ubezpieczonych cudzoziemców, bo aż 57,7%, stanowiły osoby zatrudnione na podstawie umowy o pracę, a 36,4% – zleceniobiorcy.', NOW()),
  ('Wskaźnik pokrycia wydatków FUS (2024): W I kwartale 2024 r. Fundusz Ubezpieczeń Społecznych (FUS) pokrył swoje wydatki (głównie na świadczenia) wpływami ze składek w wysokim stopniu, osiągając wskaźnik pokrycia na poziomie 85,5%.', NOW()),
  ('Wzrost średniego świadczenia (I półrocze 2025): W pierwszym półroczu 2025 r. przeciętne świadczenie emerytalno-rentowe wypłacane przez ZUS wyniosło 3986,91 zł, co oznacza wzrost o 9,1% rok do roku.', NOW()),
  ('Prognoza minimalnej emerytury (marzec 2025): Od 1 marca 2025 r. najniższa emerytura w Polsce, zgodnie z zapowiedzią waloryzacji wskaźnikiem 105,5%, wzrośnie do kwoty 1878,91 zł brutto (wzrost o 97,95 zł).', NOW()),
  ('Wydatki FUS na emerytury i renty (I półrocze 2025): W pierwszej połowie 2025 r. Fundusz Ubezpieczeń Społecznych przeznaczył na wypłatę emerytur i rent aż 198 mld zł.', NOW()),
  ('Wpływ przedłużenia aktywności zawodowej: ZUS szacuje, że wydłużenie aktywności zawodowej o jeden rok zwiększa wysokość przyszłej emerytury o około 8-12%.', NOW());